DROP DATABASE IF EXISTS studentdb;

CREATE DATABASE IF NOT EXISTS studentdb 
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

USE studentdb;

CREATE TABLE IF NOT EXISTS student (
    id INT NOT NULL PRIMARY KEY,
    st_name VARCHAR(100) NOT NULL,
    academy_id VARCHAR(100) NOT NULL,
    star INT NOT NULL,
    weapon_type INT NOT NULL,
    attack_type INT NOT NULL,
    defence_type INT NOT NULL,
    img INT NOT NULL,
    pri_weapon_img VARCHAR(20) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

CREATE TABLE IF NOT EXISTS academy (
    id INT NOT NULL PRIMARY KEY,
    aca_id INT NOT NULL,
    club VARCHAR(100) NOT NULL,
    club_img VARCHAR(2000) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

CREATE TABLE IF NOT EXISTS academyid (
    id INT NOT NULL PRIMARY KEY,
    aca_name VARCHAR(100) NOT NULL,
    aca_logo VARCHAR(200) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

CREATE TABLE IF NOT EXISTS weapon (
    id INT NOT NULL PRIMARY KEY,
    weapon VARCHAR(100) NOT NULL,
    weapon_img VARCHAR(200) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

CREATE TABLE IF NOT EXISTS Atype (
    id INT NOT NULL PRIMARY KEY,
    types VARCHAR(100) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

CREATE TABLE IF NOT EXISTS Dtype (
    id INT NOT NULL PRIMARY KEY,
    types VARCHAR(100) NOT NULL
) ENGINE = InnoDB
  DEFAULT CHARACTER SET utf8mb4 
  DEFAULT COLLATE utf8mb4_general_ci;

